package week1.day1;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int fact=1;
		//logic : fact = fact*i
		for(int i=5 ; i>0; i--) {
		fact=fact*i;   ///fact=5*4=20*3=60*2=120*1=120
		}
		System.out.println(fact);
		
		
	}

}
